    private void CalcularNotasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularNotasActionPerformed
        // TODO add your handling code here:        

        Total   = Integer.valueOf(qtdDinheiro.getText());
        qtd100  = Total /100;
        qtd50   = (Total - qtd100*100)/50;
        qtd20   = (Total - qtd100*100 - qtd50*50)/20;
        qtd10   = (Total - (qtd100*100) - (qtd50*50)-(qtd20*20))/10;
        qtd5    = (Total - (qtd100*100) - (qtd50*50)-(qtd20*20)- (qtd10*10))/5;
        qtd2    = (Total - (qtd100*100) - (qtd50*50)-(qtd20*20)- (qtd10*10)- (qtd5*5))/1;
                
        qtdNotas100.setText(String.valueOf(qtd100));
        QtdNotas50.setText(String.valueOf(qtd50));
        QtdNotas20.setText(String.valueOf(qtd20));
        QtdNotas10.setText(String.valueOf(qtd10));
        QtdNotas5.setText(String.valueOf(qtd5));
        QtdNotas2.setText(String.valueOf(qtd2));
    }//GEN-LAST:event_CalcularNotasActionPerformed

